import axios from "axios";
import React,{useState} from "react";
import SideBar from "../../components/sidebar/SideBar";
import { useGlobalStateValue } from "../../globalState/Context";
import classes from "./UserSettings.module.css";

const UserSettings = () => {

    const {user} = useGlobalStateValue()
    const [userDetails,setUserDetails]=useState({
      name:user.name,
      email:user.email,
      password:user.password,
      detailsChanged:false,
    })

    const {name, email, password,detailsChanged} = userDetails

    const handleUserDetailsChange=(name)=>(event)=>{
      setUserDetails({...userDetails,
        [name]:event.target.value,
        detailsChanged:true})
    }

    const sendUpdatedData=async()=>{
        await axios.put(`/update/${user._id}`,{
          name,
          email,
          password
        })
        setUserDetails({...userDetails,detailsChanged:false})
        window.location.reload()
    }

    return (
      <div className={classes.settings__container}>
        <div className={classes.settings__wrapper}>
          <div className={classes.settings__title}>
            <span className={classes.settings__title__update}>
              Update Your Account
            </span>
            <span className={classes.settings__title__delete}>
              Delete Account
            </span>
          </div>
          <form className={classes.settings__form}>
            <label>Profile Picture</label>
            <div className={classes.settings__profile__pic}>
              <img
                src={user.profilePic}
                alt=""
              />
              <label htmlFor="fileInput">
                <i className="far fa-user-circle"></i>{" "}
              </label>
              <input
                id="fileInput"
                type="file"
                style={{ display: "none" }}
                className={classes.settings__input}
              />
            </div>
            <label>Username</label>
            <input
              type="text"
              placeholder="Name"
              name="name"
              onChange={handleUserDetailsChange("name")}
              value={name}
              autoComplete="off"
            />
            <label>Email</label>
            <input
              type="email"
              placeholder="Email"
              name="email"
              onChange={handleUserDetailsChange("email")}
              value={email}
              autoComplete="off"
            />
            <label>Password</label>
            <input
              type="password"
              placeholder="Password"
              name="password"
              onChange={handleUserDetailsChange("password")}
              value={password}
              autoComplete="off"
            />
            {detailsChanged&&
            <button 
            className={classes.settings__submit} 
            type="submit" 
            >
              Update
            </button>}
          </form>
        </div>
        <SideBar />
      </div>
    );
};

export default UserSettings;
