import React ,{useState,useEffect} from 'react'
import { useLocation } from 'react-router'
import axios from 'axios'
import classes from './SinglePost.module.css'

const img="https://images.pexels.com/photos/3408744/pexels-photo-3408744.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=940"


const SinglePost = () => {
    const currLocation=useLocation()
    const postId=currLocation.pathname.split("/")[2]

    const [postData,setPostData] = useState({})
    useEffect(()=>{
        const fetchSinglePost = async()=>{
            const res = await axios.get(`/post/${postId}`)
            console.log(res)
            setPostData(res.data)
        }
        fetchSinglePost()
    },[postId])

    return (
        <div className={classes.single__post}>
            <div className={classes.post__container}>
                <img src={img} alt="post" className={classes.post__image} />
                <h1 className={classes.post__title}>
                    {postData.title}
                    <div className={classes.post__edit}>
                        <i className="far fa-edit"></i>
                        <i className="far fa-trash-alt"></i>
                    </div>
                </h1>
                <div className={classes.post__info}>
                    <span className={classes.post__author}>Author:{postData.name}</span>
