import React,{useState} from 'react'
import classes from './Signup.module.css'
import { Link } from 'react-router-dom'
import axios from 'axios'

const Signup = () => {

    const [userData,setUserData]=useState({
        name:'',
        email:'',
        password:'',
        erro
    })
    const {name,email,password} = userData

    const handleInputChange=(name)=>(event)=>{
        setUserData[name]=event.target.value
    }

    const handleSubmit=async(event)=>{
        event.preventDefault()
        const res = await axios.post("/signup",{
            name,
            email,
            password
        })
        console.log(res)
    }
    return (
        <div className={classes.signup__contaier}>
            <span className={classes.signup__title}>Register</span>
            <form className={classes.signup__form} onSubmit={handleSubmit}>
                <label>Username</label>
                <input className={classes.signup__input} 
                type="text" placeholder="Username..." 
                onChange={e=>handleInputChange("name",e)}/>

                <label>Email</label>
                <input className={classes.signup__input} 
                type="text" placeholder="Email..." 
                onChange={e=>handleInputChange("email",e)}/>

                <label>Password</label>
                <input className={classes.signup__input} 
                type="password" placeholder="Password..."
                onChange={e=>handleInputChange("password",e)} />

                <button className={classes.signup__button} 
                type="submit">
                    Register
                </button>
            </form>
            <Link to="/Login">
                <button className={classes.login__button}>Login</button>
            </Link>
        </div>
    )
}

export default Signup
