import React,{useState,useEffect} from 'react'
import classes from './NewPost.module.css'
import axios from 'axios'
import {defaultNewPost} from '../../constants/constants'

const NewPost = () => {

    const [postData,setPostData]=useState({
        title:'',
        description:'',
        picture:null,
        category:''
    })

    // useEffect(()=>{
    //     const sendPostData=async()=>{
    //         try{
    //             const res = await axios.post('/post/create')
    //         }catch(err){
    //             console.log(err)
    //         }
    //     }
    //     sendPostData()
    // },[])

    const handleSubmit=async()=>{
        try{
                const res = await axios.post('/post/create')

        }catch(err){
            console.log(err)
        }
    }
    const handleChange=(name)=>(event)=>{
        event.preventDefault()
        setPostData({...postData, [name]:event.target.value})
    }

    return (
        <div className={classes.new__post}>
            <img
                className={classes.post__image}
                src={defaultNewPost}
                alt=""
            />
            <form className={classes.post__form}>
                <div className={classes.post__form__group}>

                <label htmlFor="fileInput" className={classes.label}>
                    <i className="fas fa-plus"></i>
                </label>
                <input 
                id="fileInput" 
                type="file" style={{ display: "none" }} 
                onChange={handleChange("picture")}/>

                <input
                    className={classes.post__input}
                    placeholder="Title"
                    type="text"
                    autoFocus={true}
                    onChange={handleChange("title")}
                />

                <input
                    className={classes.post__input}
                    placeholder="Category"
                    type="text"
                    autoFocus={true}
                    onChange={handleChange("category")}
                />
                </div>

                <div className={classes.post__form__group}>
                <textarea
                    className={[classes.post__input,classes.post__text].join(" ")}
                    placeholder="Tell your story..."
                    type="text"
                    autoFocus={true}
                    onChange={handleChange("description")}
                />
                </div>
                <button className={classes.post__submit} type="submit">
                Publish
                </button>
            </form>
    </div>
    )
}

export default NewPost
